<script lang="ts">
import { defineComponent, watch, nextTick, inject, onMounted } from 'vue-demi'
export default defineComponent({
    props: {
        state: Number,
        success: Number,
        fail: Number,
        duplicate: Number,
        scanned: Number,
        capture: Boolean,
        inFloat: {
            type: Boolean,
            default: true,
        },
    },
    setup(props) {
        if (props.inFloat) {
            const refresh = inject<() => void>('refresh')
            watch(
                () => props,
                async () => {
                    await nextTick()
                    refresh && refresh()
                },
                {
                    deep: true,
                },
            )
            onMounted(async () => {
                console.log('onmounted')
                await nextTick()
                refresh && refresh()
            })
        }
        return {}
    },
})
</script>

